    async function main() {
        const App = await init_ethers();

        _print(`Initialized ${App.YOUR_ADDRESS}\n`);
        _print("Reading smart contracts...\n");

       const QuestionMark_CHEF_ADDR = "0x1e7c1569367E75B5eAEBad549e5FD7c9fc9746d6";
       const rewardTokenTicker = "QuestionMark";
       const QuestionMark_CHEF = new ethers.Contract(QuestionMark_CHEF_ADDR, QuestionMark_CHEF_ABI, App.provider);
        console.log(QuestionMark_CHEF);

       const startBlock = await QuestionMark_CHEF.startBlock();
       const currentBlock = await App.provider.getBlockNumber();

       const multiplier = await QuestionMark_CHEF.getMultiplier(currentBlock, currentBlock + 1);

       let rewardsPerWeek = 0
       if(currentBlock < startBlock){
         _print(`Rewards start at block <a href="https://polygonscan.com/block/countdown/${startBlock}" target="_blank">${startBlock}</a>\n`);
       }else{
        rewardsPerWeek = await QuestionMark_CHEF.QuestionPerBlock() /1e18
          * 604800 * multiplier / 2.1;
       }

        const tokens = {};
        const prices = await getMaticPrices();


        // await loadMaticChefContract(App, tokens, prices, QuestionMark_CHEF, QuestionMark_CHEF_ADDR, QuestionMark_CHEF_ABI, rewardTokenTicker,
        //     "QuestionMark", null, rewardsPerWeek, "pendingQuestion");

    await loadMaticChefContract(App, tokens, prices, QuestionMark_CHEF, QuestionMark_CHEF_ADDR, QuestionMark_CHEF_ABI, rewardTokenTicker,
        "Question", null, rewardsPerWeek, "pendingQuestion", [1]);


        hideLoading();
      }
